package oops;

class employee {
	public int empid;
	public String name;
	public int salary;

}

public class class_object {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		employee e = new employee();
		e.empid = (102);
		e.name = ("kajal");
		e.salary = (5000);
		System.out.println(e.empid);
		System.out.println(e.name);
		System.out.println(e.salary);
	}
}
